import {useState, useEffect} from 'react'

function MyForm() {

	const [data, setData] = useState({
    data: []
  })

	useEffect(() => {
		fetch("/users")
		.then( res => res.json())
		.then(data => setData({data: res}))
		}, [])

	return (
    <div className="Test">
	{data}

  </div>

	)

}
