        method: 'GET'
        })
         .then(res => {
             console.log(res);
             return res.json()
          })
         .then(users => { 
             console.log(users); 
             this.setState({ users })
          });
      }
render() {
     return (
         <div className="Test">
             <h1>Users</h1>
             {this.state.users.map(user =>
             <div key={user.id}>user: {user.name} </div>
           )}
         </div>
     );
 }
  
}
export default MyForm;

