import React, { useState } from "react";
import axios from 'axios';




export default function ViewCapsule(input){


    const [vars, setVars] = useState({
        fname: "",
        Title: "",
        Date: "",
        City: "",
        State: "",
        Country: "",
        Msg:"",
        Time:""
      })

      const [state, setState] = useState({
        //   id:'', 
           name:'',
           users: [],
           Got: false
         });


  const handleChange = ({ target }) => {
    const { name, value } = target;
    setState({ [name]: value });
  };

    let [inSearch, setSearch] = React.useState(false);

    function search(){
        setSearch(true)
    }



    if(inSearch){


   //  setState({name: JSON.stringify(Name)});
     axios.get('/users') 
     .then((response) => {
       const data = response.data;
       setState({ users: data }); 
       console.log('Data has been received!!');
     //  console.log(data);
     })  
     .catch(() => {
       console.log('Error retrieving data!!!');
     });

   //console.log('State: ', state.users);

     //JSX
   return(

       <div className="blog-">

         {displayUsers(state.users)}
         <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
       </div>

    );
    }






    else{
        return(
            <div className="viewcap" >
                <h1>Enter Capsule</h1>
                Enter Date: <input type={'date'} id="date"></input>
                <br/>
                Enter Time: <input type={'time'} id="time"></input>
                <br/>
                Enter Name: <input type={'form'} id="name"></input>
                <br/>
                Enter Country: <input type={'form'} id="country"></input>
                <br/>
                Enter State: <input type={'form'} id="state"></input>
                <br/>
                Enter City: <input type={'form'} id="city"></input>
                <br/>
                Enter Title: <input type={'form'} id="title"></input>
                <br/>
                Enter Quotes from message: <input type={'form'} id="Msg"></input>
                <br/>
                <button className="button button1" onClick={() => search()}>Search</button>
                <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
            </div>
        )
    }
}

function displayUsers (users) {

    if (!users.length) return null;



    return users.map((users, index) => (
      <div key={index} className="test">
        <h3>{users.id}</h3>
        <p>{users.name}</p>
      </div>
    ));
  };
