import React from 'react'
import axios from 'axios';

class MyForm extends React.Component 
 {

	state = {
 //   id:'',
    inSearch: false,
    name:'',
    users: []
  };



  componentDidMount = () => {
    if(this.inSearch)
    {
    this.getUsers();
    }
    else
    {

      return(
        <div className="viewcap" >
            <h1>Enter Capsule</h1>

            Enter Name: <input type={'form'} id="name"></input>
            <br/>
            
            <button className="button button1" onClick={() => search()}>Search</button>
            <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
        </div>
    )
    }
  };


  getUsers = () => {
    axios.get('/users')
      .then((response) => {
        const data = response.data;
        this.setState({ users: data });
        console.log('Data has been received!!');
        console.log(data);
      })
      .catch(() => {
        alert('Error retrieving data!!!');
      });
  }

  search =() => {
    this.setState({inSearch: true});
}

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  displayUsers = (users) => {

    if (!users.length) return null;



    return users.map((users, index) => (
      <div key={index} className="test">
        <h3>{users.id}</h3>
        <p>{users.name}</p>
      </div>
    ));
  };
 
  render() {

    console.log('State: ', this.state.users);

    //JSX
 
    return(
      
        <div className="test">
          {this.displayUsers(this.state.users)}
        </div>
    
    );
    

  }
  
}
