import React, { useState } from "react";
import axios from 'axios';
import MyForm from './MyForm';

function displayUsers (users) {

    if (!users.length) return null;



    return users.map((users, index) => (
      <div key={index} className="test">
        <h3>{users.id}</h3>
        <p>{users.name}</p>
      </div>
    ));
  };

