import React, { useState } from "react";




function submit(input, state){
     // insert(Name,Title,Message,City,State,Country);

     input.setPage("home");
     alert("Posted as " + state.Title);
    // var insert = require('./DBFunctions.js');
     //insert(state.fname, state.Title, state.Msg, state.City, state.State, state.Country);

 }

export default function AddToCapsule(input){
  const connection = mysql.createConnection({
    host     : 'localhost',
    database : 'capsule',   // the name of your db
    user     : 'root',     // your root username
    password : 'your_current_password'
  â€‚
  });



    const [state, setState] = useState({
        fname: "",
        Title: "",
        Msg: "",
        City: "",
        State: "",
        Country: ""
      })

      const handleChange = e => {
        setState({
          ...state,
          [e.target.name]: e.target.value,
        })
      }


    return(
        <div className="capsuleheader">
            <h1>Add To Capsule</h1>

          {/* <br/> */}

          Name (optional):
          <input
            type="text"
            name="fname"
            value={state.fname}
            onChange={handleChange}
          />

        <br/>

          Title:
          <input
            type="text"
            name="Title"
            value={state.Title}
            onChange={handleChange}
          />

        <br/>

          Message:
          <input
            type="text"
            name="Msg"
            value={state.Msg}
            onChange={handleChange}
          />

        <br/>

          City (optional):
          <input
            type="text"
            name="City"
            value={state.City}
            onChange={handleChange}
          />

        <br/>

          State (optional):
          <input
            type="text"
            name="State"
            value={state.State}
            onChange={handleChange}
          />

        <br/>

          Country (optional):
          <input
            type="text"
            name="Country"
            value={state.Country}
            onChange={handleChange}
          />


      <h5>
        Post: {state.fname} from {state.Country}, {state.State}, {state.City} says in {state.Title} the following, {state.Msg}
      </h5>

            <button className="button button1" onClick={() => submit(input, state)}>Submit</button>
            <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
        </div>
    )
}
