import React from 'react'
import axios from 'axios';

class MyForm extends React.Component 
 {

	state = {
    users: []
  };



  componentDidMount = () => {
    this.getUsers();
  };


  getUsers = () => {
    axios.get('/users')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been received!!');
      })
      .catch(() => {
        alert('Error retrieving data!!!');
      });
  }

}
