import React, { useState } from "react";
import axios from 'axios';




export default function ViewCapsule(input){

    /** 
    const [vars, setVars] = useState({
        fname: "",
        Title: "",
        Date: "",
        City: "",
        State: "",
        Country: "",
        Msg:"",
        Time:""
      }) */

      const [state, setState] = useState({
        //   id:'', 
           fname:'',
           Title:'',
            City:'',
            State:'',
            Country:'',
            Msg:'',
            Time:'',
            EndTime:'',
           users: [],
         });



         const handleChange = e => {
            setState({
              ...state,
              [e.target.name]: e.target.value,
            })
          }

    let [inSearch, setSearch] = React.useState(false);


    function search(){
        console.log(state.Time);
        console.log(state.EndTime);
        axios.get('/users', { params: { fname: state.fname, sTime: state.Time, eTime: state.EndTime, Country: state.Country, City: state.City, State: state.State, Title: state.Title, Msg: state.Msg } }) 
        .then((response) => {
          const data = response.data;
          setState({ users: data }); 
          console.log('Data has been received!!');
        //  console.log(data);
        })  
        .catch(() => {
          console.log('Error retrieving data!!!');
        });

        setSearch(true)
    }

    function displayUsers (users) {

        if (!users.length) return null;



        return users.map((users, index) => (
          <div key={index} className="test">
            <h3>{users.Title} by {users.Name}</h3> 
            <br> <b>Posted on: {users.created_at}</b></br>
            <br><u>From {users.City} {users.State} {users.Country}</u></br>
            <br> <p>{users.Msg}</p></br>
          </div>
        ));
      };


    if(inSearch){


   return(

       <div className="blog-">

         {displayUsers(state.users)}
         <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
       </div>

    );
    }






    else{
        return(
            <div className="viewcap" >
                <h1>Enter Capsule</h1>

                Enter Start Time: <input
                    type="datetime-local"
                    name="Time"
                    value={state.Time}
                    onChange={handleChange}
                />
                <br/>
                Enter End Time: <input
                    type="datetime-local"
                    name="EndTime"
                    value={state.EndTime}
                    onChange={handleChange}
                />
                <br/>
                Enter  Name:
                <input
                    type="text"
                    name="fname"
                    value={state.fname}
                    onChange={handleChange}
                />
                <br/>
                Enter Country: <input
                    type="text"
                    name="Country"
                    value={state.Country}
                    onChange={handleChange}
                />
                <br/>
                Enter State: <input
                    type="text"
                    name="State"
                    value={state.State}
                    onChange={handleChange}
                />
                <br/>
                Enter City: <input
                    type="text"
                    name="City"
                    value={state.City}
                    onChange={handleChange}
                />
                <br/>
                Enter Title: <input
                    type="text"
                    name="Title"
                    value={state.Title}
                    onChange={handleChange}
                />
                <br/>
                Enter Quotes from message: <input
                    type="text"
                    name="Msg"
                    value={state.Msg}
                    onChange={handleChange}
                />
                <br/>
                <button className="button button1" onClick={() => search()}>Search</button>
                <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
            </div>
        )
    }
}
