import React from 'react'

class MyForm extends React.Component{
  constructor(props){
      super(props);
      this.state={ posts : [] };

      fetch('http://localhost:3000/posts')
          .then(response =>{
              response.json();
          })
          .then(posts => {
              this.setState({posts})
          })
          .then( (err) => {
              console.log(err);
          })
  }
  render(){
      return(
          <div>
              <ul>
                  {this.state.posts.map( post => 
                  <p>
                      <li>Some Text_1: {post.db_col_1}</li>

                  </p>
                  )}
              </ul>
          </div>
      )
  }
}
export default MyForm;
