import React, { useState } from 'react'
import axios from 'axios';
/** 

function displayUsers (users) {

  if (!users.length) return null;



  return users.map((users, index) => (
    <div key={index} className="test">
      <h3>{users.id}</h3>
      <p>{users.name}</p>
    </div>
  ));
};
*/

export default function MyForm(input/** , Name */) 
{
/** 
	const [state, setState] = useState({
 //   id:'', 
    name:'',
    users: [],
    Got: false
  });

  const handleChange = ({ target }) => {
    const { name, value } = target;
    setState({ [name]: value });
  };

   //  setState({name: JSON.stringify(Name)});
     axios.get('/users') 
    .then((response) => {
      const data = response.data;
      setState({ users: data }); 
      console.log('Data has been received!!');
    //  console.log(data);
    })  
    .catch(() => {
      console.log('Error retrieving data!!!');
    });
  
  //console.log('State: ', state.users);

    //JSX
    */
  return(
      
      <div className="blo">
          
        <button className="button button1" onClick={() => input.setPage('home')}>Go Back</button>
      </div>
    
  );
}

/** 
class MyForm extends React.Component 

{
  
  constructor()
  {
    super();
    this.state ={users: []};
}
componentDidMount() {
       fetch('http://localhost:3000/users',{
        method: 'GET'
        })
         .then(res => {
             console.log(res);
             return res.json()
          })
         .then(users => { 
             console.log(users); 
             this.setState({ users })
          });
      }
render() {
     return (
         <div className="Test">
             <h1>Users</h1>
             {this.state.users.map(user =>
             <div key={user.id}>user: {user.name} </div>
           )}
         </div>
     );
 }
  
}
export default MyForm;

 /** 
class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { name: '' };
  }
 
  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value});
  }
 
  handleSubmit = (event) => {
    alert('A form was submitted: ' + this.state.name);
 
    fetch('http://localhost:3000/display-data', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        // We convert the React state to JSON and send it as the POST body
      body: JSON.stringify({
        body: this.state.name})
      })
      
      .then(function(response) {
        
        console.log(response)
        
        return response.json();
      });
 
    event.preventDefault();
}
 
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} name="name" onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  } 
}
